import React, { Component } from 'react'
import './App.scss';
import Header from './Components/Header/header.jsx';
import Video from './Components/Video/video.jsx';
import Comments from './Components/Comments/comments.jsx';
import Suggestions from './Components/Suggestions/suggestions.jsx';
import Poster from './Assets/Images/video-list-0.jpg';
import TopVideo from './Assets/Video/BrainStationSampleVideo.mp4';
import image1 from './Assets/Images/video-list-1.jpg';
import image2 from './Assets/Images/video-list-1.jpg';
import image3 from './Assets/Images/video-list-1.jpg';
import image4 from './Assets/Images/video-list-1.jpg';
import image5 from './Assets/Images/video-list-1.jpg';
import image6 from './Assets/Images/video-list-1.jpg';
import image7 from './Assets/Images/video-list-1.jpg';
import image8 from './Assets/Images/video-list-1.jpg';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mainVideo: {
        id: '0',
        title: 'BMX Rampage: 2018 Highlights',
        description: 'On a gusty day in Southern Utah, a group of 25 daring mountain bikers blew the doors off what is possible on two wheels, unleashing some of the biggest moments the sport has ever seen. While mother nature only allowed for one full run before the conditions made it impossible to ride, that was all that was needed for event veteran Kyle Strait, who won the event for the second time -- eight years after his first Red Cow Rampage title',
        channel: 'Red Cow',
        image: Poster,
        views: '1,001,023',
        likes: '110,985',
        duration: '0:45',
        video: TopVideo,
        timestamp: '12/18/2018',
        comments: 'type of <array>'
      },

      sideVideo: [
        {
          id: '1',
          title: 'Become A Travel Pro In One Easy Lesson...',
          channel: 'Scotty Cranmer',
          image: { image1 }
        },
        {
          id: '2',
          title: 'Les Houches The Hidden Gem Of The...',
          channel: 'Scotty Cranmer',
          image: { image2 }
        },
        {
          id: '3',
          title: 'Travel Health Useful Medical Information...',
          channel: 'Scotty Cranmer',
          image: { image3 }
        },
        {
          id: '4',
          title: 'Cheap Airline Tickets Great Ways To Save',
          channel: 'Emily Harper',
          image: { image4 }
        },
        {
          id: '5',
          title: 'Take A Romantic Break In A Boutique Hotel',
          channel: 'Ethan Owen',
          image: { image5 }
        },
        {
          id: '6',
          title: 'Choose the Perfect Accommodations',
          channel: 'Lydia Perez',
          image: { image6 }
        },
        {
          id: '7',
          title: 'Cruising Destination Ideas',
          channel: 'Timothy Austin',
          image: { image7 }
        },
        {
          id: '8',
          title: 'Train Travel On Track For Safety',
          channel: 'Scotty Cranmer',
          image: { image8 }
        },
      ]
    }
  }

  render() {
    return (
      <div className="App">
        <Header />
        <Video
          mainVideoProp={this.state.mainVideo}
        />
        <Comments />
        <Suggestions
          sideVideoProp={this.state.sideVideo}
        />
      </div>
    )
  }
}

export default App;
